              data = {props.personasaCargo}
              columns={[
                {
                  id: 1,
                  Header: "Nombres y apellidos de las personas a cargo",
                  accessor: "nombre",
                  width: 500,
                  headerClassName: "encabezado_columna",
                  Cell: renderEditable
                },
                {
                  id: 2,
                  Header: "Parentesco",
                  accessor: "parentesco",         
                  headerClassName: "encabezado_columna",                           
                  Cell: renderEditable
                },
                {
                  id: 3,
                  Header: "Edad",
                  accessor: "edad",              
                  width: 120,                                    
                  className: "text-center",
                  headerClassName: "encabezado_columna",
                  Cell: renderEditable
                },
                {
                  id: 4,
                  Header: "Eliminar",
                  accessor: "delete",              
                  width: 250,
                  className: "text-center",
