import React from 'react';
import { useForm } from 'react-hook-form'

import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

import Label from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


import 'bootstrap/dist/css/bootstrap.min.css';

import '../styles/app.css';

function Main() {

  const { register, handleSubmit, watch, errors } = useForm();

  // your form submit function which will invoke after successful validation
  const onSubmit = data => {
    console.log(data);
  }; 


  console.log(watch("example")); // you can watch individual input by pass the name of the input

  return (

    <Container fluid>
      <h2 className="text-center mb-5">SOLICITUD DE CRÉDITO</h2>


      <h3 className="mb-3 mt-5">1. Información personal del funcionario</h3>

      <Form onSubmit={handleSubmit(onSubmit)} >
        <Row className="mb-3">
          <Col sm="3" md="2">
            <Label>Nombres y Apellidos completos</Label>
          </Col>
          <Col>
            <Form.Control name="nombres" type="text" ref={register} />
          </Col>
        </Row> 
        <Row>
          <Col>
            <Label>Email address</Label>
          </Col>
          <Col>
            <Form.Control name="email" type="email" placeholder="Enter email" ref={register} />
          </Col>
        </Row>                    

      </Form>


    </Container>
  );
}

export default Main;


